public class SumOfNumbers {		//class name is Sum but the question is to print product

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//Declaring values to the variables
		int Num1 = 10;
		int Num2 = 15;
		int Num3 = 20;
		
		//PRINTING THE RESULT
		System.out.println("The product of these numbers is : ");
		System.out.println(Num1 * Num2 * Num3);	//calculation
	}

}
