public class evaluate {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// Printing a statement then printing the evaluation
		System.out.println("4+6*2 is:");
		System.out.println(4+6*2);
		
		// Printing a statement then printing the evaluation
		System.out.println("10/5 + 8 is:");
		System.out.println(10/5 + 8);
		
		// Printing a statement then printing the evaluation
		System.out.println("12/4 + 16/2 is:");
		System.out.println(12/4 + 16/2);
		
		// Printing a statement then printing the evaluation
		System.out.println("17/2 is:");
		System.out.println(17/2);
		
		// Printing a statement then printing the evaluation
		System.out.println("25-10/2 is:");
		System.out.println(25-10/2);
	}

}
